import React,{Component} from 'react';
import {Card,Form, Button, Col} from 'react-bootstrap';
import { Icon } from 'semantic-ui-react'
import axios from 'axios';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default class OrderProduct extends Component{
    constructor(props){
        super(props);
        this.state = this.initialState;
    }
    initialState = {
        id:'', date:new Date(), c_date:new Date(), order_type:'', order_status:'', empId:''
    };

    componentDidMount(){
        const stored_order = localStorage.getItem("STORED_ORDER");
        if(stored_order){
            this.setState({
                id:stored_order.id,
                date:stored_order.date,
                c_date:stored_order.c_date,
                order_type:stored_order.order_type,
                order_status:stored_order.order_status,
                empId:stored_order.empId
            });
        }
    }

    submitOrder = event =>{
        event.preventDefault();
        const order={
            id:this.state.id,
            date:this.state.date,
            c_date:this.state.c_date,
            order_type:this.state.order_type,
            order_status:this.state.order_status,
            empId:this.state.empId
        };

        alert(order.order_type);
        if(localStorage.getItem("STORED_ORDER")){
            localStorage.removeItem("STORED_ORDER");
        }
        localStorage.setItem("STORED_ORDER",JSON.stringify(order));

        const stored_order = localStorage.getItem("STORED_ORDER");
        alert(JSON.parse(stored_order).empId);

        return this.props.history.push("/chooseProduct");
    }

    resetOrder = () =>{
        this.setState(this.initialState);
    }

    infoChange = event =>{
        event.preventDefault();
        this.setState({
            [event.target.name]:event.target.value
        });
    }

    handleStartDateChange = sDate =>{
        this.setState({
            date:sDate
        });
    }

    handleEndDateChange = eDate =>{
      this.setState({
            c_date:eDate
      });
   }

   typeChange = (type) =>{
       this.setState({
        order_type:type
       });
   }

   statusChange = (status) =>{
       this.setState({
        order_status:status
       });
   }



    render(){
        return(
            <Card className="border border-light bg-light" style={{margin: "20px"}}>
                <Card.Header><Icon name='plus square' /> {this.state.empId ? "Update" : "Create"} An Order</Card.Header>
                <Form onReset={this.resetOrder} onSubmit={this.submitOrder} id="orderFormId">
                <Card.Body>
                    <Form.Row>
                      <Form.Group as={Col} controlId="formGridName">
                        <Form.Label>Order Type</Form.Label>
                            <Form.Control as="select"
                            name="order_type" 
                            onSelect={this.typeChange}>
                                <option>purchase</option>
                                <option>sale</option>
                                <option>outbound</option>
                            </Form.Control>
                      </Form.Group>
                      <Form.Group as={Col} controlId="formGridColor">
                        <Form.Label>order_status</Form.Label>
                        <Form.Control as="select"
                            name="stored_order" 
                            onSelect={this.statusChange}>
                                <option>pending</option>
                                <option>complete</option>
                            </Form.Control>
                      </Form.Group>
                    </Form.Row>
                    <Form.Row>
                    <Form.Group as={Col} controlId="formGridSDate">
                        <Form.Label>Issue Date</Form.Label>
                        <DatePicker
                        selected={this.state.date}
                        onChange={this.handleStartDateChange}
                        />
                    </Form.Group>
                    <Form.Group as={Col} controlId="formGridEDate">
                        <Form.Label>Complete Date</Form.Label>
                        <DatePicker
                        selected={this.state.c_date}
                        onChange={this.handleEndDateChange}
                        />
                    </Form.Group>
                    </Form.Row>
                    <Form.Row>
                      <Form.Group as={Col} controlId="formGridSalePrice">
                        <Form.Label>Employee ID</Form.Label>
                        <Form.Control required autoComplete="off"
                        type="text"
                        name="empId"
                        value={this.state.empId}
                        onChange={this.infoChange}
                        className={"bg-light"} placeholder="Enter your employee id" />
                      </Form.Group>
                    </Form.Row>
                </Card.Body>
                <Card.Footer style={{"textAlign":"right"}}>
                  <Button size="sm" variant="success" type="submit">
                    <Icon name='save' /> Save
                  </Button>{" "}
                  <Button size="sm" variant="info" type="reset">
                  <Icon name='repeat' /> Reset
                  </Button>
                </Card.Footer>
                </Form>
            </Card>

    );
    }


}
