import  {FETCH_PRODUCT_REQUEST,FETCH_PRODUCT_SUCCESS,FETCH_PRODUCT_FAILURE} from './OrderType';

const initialState = {
    products: [],
    size:5,
    page: 1,
    totalPage:0,
    error: ''
};

const reducer = (state = initialState, action) => {
    switch(action.type){
        case FETCH_PRODUCT_REQUEST:
            return{
                ...state
            };
        case FETCH_PRODUCT_SUCCESS:
            return{
                products: action.payload.content,
                size:5,
                page: action.page,
                totalPage : action.payload.totalPages,
                error: ''
            };
        case FETCH_PRODUCT_FAILURE:
            return{
                products: [],
                size:5,
                totalPage:0,
                error: action.payload
            };
        default:
            return state;
    }
};

export default reducer;
