import React, {Component} from 'react';
import {Card, Table, ButtonGroup, Button} from 'react-bootstrap';

import axios from 'axios';

export default class ViewProduct extends Component{
	constructor(props){
		super(props);
		this.state={
				products:[]
		};
	}
	componentDidMount(){
		this.findAllbooks();
	}

	findAllbooks(){
		axios.get("http://localhost:8082/rest/products")
		.then(response => response.data)
		.then((data)=>{
            this.setState({products:data});
            //console.log("get data from server");
            //console.log(data);
		});
	}

	render(){
		return(
			<Card className="border border-dark bg-light">
				<Card.Header>Product List </Card.Header>
					<Card.Body>
						<Table bordered hover striped responsive="sm">
						<thead>
					    <tr>
					      <th>Name</th>
					      <th>Color</th>
					      <th>Barcode</th>
					      <th>Purchase Price</th>
					      <th>Sale Price</th>
					      <th>Brand</th>
                          <th>Size</th>
                          <th>Category</th>
                          <th>Style</th>
                          <th>Total Amount</th>
					    </tr>
					  </thead>
					  <tbody>

					  {this.state.products.length === 0 ? 
							  <tr align="center">
					           <td colSpan="10"> 0 product avaliable. </td>
					          </tr> :
					        this.state.products.map((product) => (
					        		<tr key={product.name}>
                                      <td>{product.name}</td>
					        		  <td>{product.color}</td>
					        		  <td>{product.barcode}</td>
					        		  <td>{product.prime_cost}</td>
					        		  <td>{product.sale_price}</td>
					        		  <td>{product.brand_id}</td>
                                      <td>{product.size}</td>
                                      <td>{product.category}</td>
                                      <td>{product.style}</td>
                                      <td>{product.total_amount}</td>

					        		</tr>
					        		))

					  }

					  </tbody>
						</Table>

					</Card.Body>
			</Card>

		);
	}
}
