import React, {Component} from 'react';
import {Card, Table, ButtonGroup, Button,InputGroup, FormControl} from 'react-bootstrap';
import {Link} from 'react-router-dom';
import { CardFooter } from 'reactstrap';
import axios from 'axios';

import {connect} from 'react-redux';
import {fetchProduct} from '../services/order/OrderActions';

class CreateOrder extends Component{
	constructor(props){
		super(props);
		this.state={
                amount:0
		};
	}
	componentDidMount(){
        // this.findAllProducts(this.state.page);
        this.props.fetchProduct(this.props.page,this.props.size);
	}

	// findAllProducts(page){
	// 	page-=1;
	// 	axios.get("http://localhost:8082/rest/products"+"?page="+page+"&size="+this.state.size)
	// 	.then(response => response.data)
	// 	.then((data)=>{
	// 		this.setState({
	// 			products:data.content,
	// 			totalPage:data.totalPages,
	// 		    page:data.number+1,
	// 		    totalElement:data.totalElements});
    //         //console.log("get data from server");
    //         //console.log(data);
	// 	});
	// };

	// deleteProduct = (productId)=>{
	// 	axios.delete("http://localhost:8082/rest/products/"+productId)
	// 	.then(response => {
    //         if(response.data != null){
	// 			alert("product delete Successfully");
	// 			this.setState({
	// 				products: this.state.products.filter(product => product.id !== productId)
	// 			});
	// 		}
	// 	});

	// };

	changePage = event => {
		let targetPage = parseInt(event.target.value);
        // this.findAllProducts(targetPage);
        this.props.fetchProduct(targetPage,this.props.size);
		this.setState({
			[event.target.name]:targetPage
		});
	};

	firstPage = () =>{
       if(this.props.page > 1){
        // this.findAllProducts(1);
        this.props.fetchProduct(1,this.props.size);
	   }
	};

	prevPage = () =>{
		if(this.props.page > 1){
			let prevPage=this.props.page-1;
            // this.findAllProducts(prevPage);
            this.props.fetchProduct(prevPage,this.props.size);
		}
	};

	nextPage = () =>{
        let lastPage = this.props.totalPage;
		if(this.props.page < lastPage){
            // this.findAllProducts(this.state.page+1);
            this.props.fetchProduct(this.props.page+1,this.props.size);
		}
	};

	lastPage = () =>{
		let lastPage = this.props.totalPage;
		if(this.props.page < lastPage){
            // this.findAllProducts(lastPage);
            this.props.fetchProduct(this.props.page+1,this.props.size);
		}
    };

    substractAmount = () =>{
		if(this.state.amount > 1){
            let newAmount = this.state.amount-1;
			this.setState({
               amount:newAmount
            });
		}
    };

    addAmount = () => {
       let newAmount = this.state.amount+1;
       this.setState({
           amount:newAmount
       });
    }

    changeAmount = event =>{
        let newAmount = parseInt(event.target.value);
        this.setState({
            [event.target.name] : newAmount
        });
    }

    addProduct = () =>{

    }

	render(){
			const pageCss={
				width:"45px",
				border:"1px solid #9FC2E8",
				textAlign:"center",
				fontWeight:"bold"
			}
		return(
			<Card className="border border-light bg-light" style={{margin: "20px"}}>
				<Card.Header>Product List </Card.Header>
					<Card.Body>
						<Table bordered hover striped responsive="xl">
						<thead>
					    <tr>
					      <th>Name</th>
					      <th>Color</th>
					      <th>Barcode</th>
					      <th>Purchase Price</th>
					      <th>Sale Price</th>
					      <th>Brand</th>
                          <th>Size</th>
                          <th>Category</th>
                          <th>Style</th>
                          <th>Total Amount</th>
						  <th>Select Product</th>
					    </tr>
					  </thead>
					  <tbody>

					  {this.props.products.length === 0 ? 
							  <tr align="center">
					           <td colSpan="11"> 0 product avaliable. </td>
					          </tr> :
					        this.props.products.map((product) => (
					        		<tr key={product.id}>
                                      <td>{product.name}</td>
					        		  <td>{product.color}</td>
					        		  <td>{product.barcode}</td>
					        		  <td>{product.prime_cost}</td>
					        		  <td>{product.sale_price}</td>
					        		  <td>{product.brand_name}</td>
                                      <td>{product.size}</td>
                                      <td>{product.category}</td>
                                      <td>{product.style}</td>
                                      <td>{product.total_amount}</td>
									  <td>
									  <InputGroup size="sm">
							   <InputGroup.Prepend>
							     <Button type="button" variant="outline-info" disabled={this.state.amount === 1 ? true:false}
								   onClick={this.substractAmount}>
									 -
								 </Button>
							   </InputGroup.Prepend>

							   <FormControl style={pageCss} name="amount" value={this.state.amount}
							     onChange={this.changeAmount}/>

							   <InputGroup.Append>
							   <Button type="button" variant="outline-info" onClick={this.addAmount}>
									+
								 </Button>

							   </InputGroup.Append>

							 </InputGroup>
                             {" "}
                             <Button type="button" variant="outline-info" 
								   onClick={this.addProduct}>
									 Add to Existing Order
							</Button>

										  </td>

					        		</tr>
					        		))

					  }

					  </tbody>
						</Table>

					</Card.Body>
					<CardFooter>
						<div style={{"float":"left"}}>
                             Showing Page {this.props.page} of {this.props.totalPage}
						</div>
						<div style={{"float":"right"}}>
                             <InputGroup size="sm">
							   <InputGroup.Prepend>
							     <Button type="button" variant="outline-info" disabled={this.props.page === 1 ? true:false}
								   onClick={this.firstPage}>
									 First
								 </Button>
								 <Button type="button" variant="outline-info" disabled={this.props.page === 1 ? true:false}
								   onClick={this.prevPage}>
									 Prev
								 </Button>
							   </InputGroup.Prepend>

							   <FormControl style={pageCss} name="page" value={this.props.page}
							     onChange={this.changePage}/>

							   <InputGroup.Append>
							   <Button type="button" variant="outline-info" disabled={this.props.page === this.props.totalPage ? true:false}
							     onClick={this.nextPage}>
									 Next
								 </Button>
								 <Button type="button" variant="outline-info" disabled={this.props.page === this.props.totalPage ? true:false}
								 onClick={this.lastPage}>
									 Last
								 </Button>
							   </InputGroup.Append>

							 </InputGroup>

                             <Button type="button" variant="outline-info" 
								   onClick={this.addProduct}>
									 Checkout the Order
							</Button>
						</div>
					</CardFooter>
			</Card>

		);
	}
}

const mapStateToProps = state => {
   return {
        products : state.products,
        size : state.size,
        page : state.page,
        totalPage: state.totalPage
   }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchProduct: (page,size) =>{
            dispatch(fetchProduct(page,size))
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(CreateOrder);
