import React, {Component} from 'react';
import {Card, Table, ButtonGroup, Button} from 'react-bootstrap';
import {Link} from 'react-router-dom';
import axios from 'axios';

export default class ViewProduct extends Component{
	constructor(props){
		super(props);
		this.state={
				products:[],
				page:0,
				size:10
		};
	}
	componentDidMount(){
		this.findAllProducts();
	}

	findAllProducts(){
		axios.get("http://localhost:8082/rest/products"+"?page="+this.state.page+"&size="+this.state.size)
		.then(response => response.data)
		.then((data)=>{
            this.setState({products:data.content});
            //console.log("get data from server");
            //console.log(data);
		});
	};

	deleteProduct = (productId)=>{
		axios.delete("http://localhost:8082/rest/products/"+productId)
		.then(response => {
            if(response.data != null){
				alert("product delete Successfully");
				this.setState({
					products: this.state.products.filter(product => product.id !== productId)
				});
			}
		});

	};

	render(){
		return(
			<Card className="border border-light bg-light" style={{margin: "20px"}}>
				<Card.Header>Product List </Card.Header>
					<Card.Body>
						<Table bordered hover striped responsive="xl">
						<thead>
					    <tr>
					      <th>Name</th>
					      <th>Color</th>
					      <th>Barcode</th>
					      <th>Purchase Price</th>
					      <th>Sale Price</th>
					      <th>Brand</th>
                          <th>Size</th>
                          <th>Category</th>
                          <th>Style</th>
                          <th>Total Amount</th>
						  <th>Modify Product</th>
					    </tr>
					  </thead>
					  <tbody>

					  {this.state.products.length === 0 ? 
							  <tr align="center">
					           <td colSpan="11"> 0 product avaliable. </td>
					          </tr> :
					        this.state.products.map((product) => (
					        		<tr key={product.id}>
                                      <td>{product.name}</td>
					        		  <td>{product.color}</td>
					        		  <td>{product.barcode}</td>
					        		  <td>{product.prime_cost}</td>
					        		  <td>{product.sale_price}</td>
					        		  <td>{product.brand_id}</td>
                                      <td>{product.size}</td>
                                      <td>{product.category}</td>
                                      <td>{product.style}</td>
                                      <td>{product.total_amount}</td>
									  <td>
									  <Button variant="danger" onClick={this.deleteProduct.bind(this,product.id)}>Delete Product</Button> 
									  <Link to={"edit/"+product.id} className="btn btn-info">Edit Product</Link>

										  </td>

					        		</tr>
					        		))

					  }

					  </tbody>
						</Table>

					</Card.Body>
			</Card>

		);
	}
}
